mapper.insert:
INSERT
INTO reviewcomment (content, write_date, member_id)
VALUES (
   #{content}, sysdate, #{memberVO.memberId}
)

mapper.delete:
DELETE
FROM (
   SELECT TO_CHAR(write_date, 'YYYY-MM-DD HH24:MI:SS') write_date, member_id
   FROM reviewcomment
)
WHRER
   member_id = #{memberId}
   AND write_date = #{writeDate}
